function reemplazarTextura( player, nombreParte, dirFichero )
	if not shaders[player] then
		if player then
			shaders[player] = {}
		end
	end
	
	quitarShader( player, nombreParte)
	--outputDebugString( "player: "..getPlayerName(player)..", nombreParte: "..nombreParte..", dirFichero: "..tostring(dirFichero) )
	if fileExists( dirFichero ) then
		texture = dxCreateTexture ( dirFichero )
		if texture and nombreParte and player then
			shader, tec = dxCreateShader( "shader.fx", 1000, 0, false, "ped" )
			shaders[player][nombreParte] = shader
			
			engineApplyShaderToWorldTexture ( shader, nombreParte, player )
			dxSetShaderValue ( shader, "gTexture", texture )
		end
	end
end
addEvent("ropa:shader", true)
addEventHandler("ropa:shader", getLocalPlayer(), reemplazarTextura)


function quitarShader( player, nombreParte )
	if shaders[player] then 
		shader = shaders[player][nombreParte]
		if shader then
			engineRemoveShaderFromWorldTexture( shader, nombreParte, player )
		end
	end
end
addEvent("ropa:quitar_shader", true)
addEventHandler("ropa:quitar_shader", getRootElement(), quitarShader)

function ponerRopa(player, ropa1, ropa2, ropa3, raza)

removePedClothes( player, 17 )
addPedClothes ( player, ropa[tonumber(ropa1)].Texture, ropa[tonumber(ropa1)].Model, ropa[tonumber(ropa1)].tipo )
addPedClothes ( player, ropa[tonumber(ropa2)].Texture, ropa[tonumber(ropa2)].Model, ropa[tonumber(ropa2)].tipo )
addPedClothes ( player, ropa[tonumber(ropa3)].Texture, ropa[tonumber(ropa3)].Model, ropa[tonumber(ropa3)].tipo )

if ropa[tonumber(ropa1)].raza then
	reemplazarTextura( player, partecuerpo[ropa[tonumber(ropa1)].tipo], "texturas/".. razas[raza].."/"..ropa1..".png" )
else
	reemplazarTextura( player, partecuerpo[ropa[tonumber(ropa1)].tipo], "texturas/Mezcla/"..ropa1..".png" )
end
if ropa[tonumber(ropa2)].raza then
	reemplazarTextura( player, partecuerpo[ropa[tonumber(ropa2)].tipo], "texturas/".. razas[raza].."/"..ropa2..".png" )
else
	reemplazarTextura( player, partecuerpo[ropa[tonumber(ropa2)].tipo], "texturas/Mezcla/"..ropa2..".png" )
end
if ropa[tonumber(ropa3)].raza then
	reemplazarTextura( player, partecuerpo[ropa[tonumber(ropa3)].tipo], "texturas/".. razas[raza].."/"..ropa3..".png" )
else
	reemplazarTextura( player, partecuerpo[ropa[tonumber(ropa3)].tipo], "texturas/Mezcla/"..ropa3..".png" )
end
	
end
addEvent("ropa:poner", true)
addEventHandler("ropa:poner", localPlayer, ponerRopa)

function getTablaTipo(tipo)
	local tabla = {}
	for i, ropa in pairs(ropa) do
		if ropa.tipo == tipo then
		table.insert(tabla, i)
		end
	end
	return tabla
end

function getRopaData( id )
	return ropa[tonumber(id)]
end
