function vestidorClick ( button, state )
    if vestidor and button=="left" and state=="down" then
    	if isCursorHover(screenW * 0.4380, screenH * 0.7194, screenW * 0.1083, screenH * 0.0481) then
    		vestidor = false
    		showCursor(false)
    	elseif isCursorHover(screenW * 0.3990, screenH * 0.2870, screenW * 0.1870, screenH * 0.0926) and vestidor[v_index] ~= nil then
    		triggerServerEvent('actualizar:ropa', localPlayer, localPlayer, vestidor[v_index].ropa_id, ropa[vestidor[v_index].ropa_id].tipo)
    	elseif isCursorHover(screenW * 0.3990, screenH * 0.3944, screenW * 0.1870, screenH * 0.0926) and vestidor[v_index+1] ~= nil then
    		triggerServerEvent('actualizar:ropa', localPlayer, localPlayer, vestidor[v_index+1].ropa_id, ropa[vestidor[v_index+1].ropa_id].tipo)
    	elseif isCursorHover(screenW * 0.3990, screenH * 0.5009, screenW * 0.1870, screenH * 0.0926) and vestidor[v_index+2] ~= nil then
    		triggerServerEvent('actualizar:ropa', localPlayer, localPlayer, vestidor[v_index+2].ropa_id, ropa[vestidor[v_index+2].ropa_id].tipo)
    	elseif isCursorHover(screenW * 0.3990, screenH * 0.6074, screenW * 0.1870, screenH * 0.0926) and vestidor[v_index+3] ~= nil then
    		triggerServerEvent('actualizar:ropa', localPlayer, localPlayer, vestidor[v_index+3].ropa_id, ropa[vestidor[v_index+3].ropa_id].tipo)
    	end
    end
end
